  `legar_person_phone` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '法定代表人/负责人电话',
  `legar_idcard` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '法人身份证号',
  `idcard_area` tinyint NULL DEFAULT NULL COMMENT '身份证地区：1中国；2中国香港；3中国澳门；4中国台湾；5其他国家',
  `creditor_account` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '债权人本人账号',
  `creditor_account_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '债权人账户户名',
  `creditor_opening_bank` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '债权人开户行',
  `address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '联系地址',
  `postcode` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '邮编',
  `email` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '邮箱',
  `weChat` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '微信号',
  `creditor_total` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '申报债权总额（元）',
  `interest` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '本金、利息、违约金、损害赔偿金等(元）',
  `other_total` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '其他（诉讼费、仲裁费、鉴定费等）',
  `creditor_create_date` date NULL DEFAULT NULL COMMENT '债权发生时间',
  `creditor_is_expire` tinyint NULL DEFAULT 0 COMMENT '债权是否到期，0未到期、1到期',
  `creditor_expire_date` date NULL DEFAULT NULL COMMENT '债权到期截止时间',
  `last_request_date` date NULL DEFAULT NULL COMMENT '最后一次要求清偿时间',
  `is_guarantee` tinyint NULL DEFAULT 0 COMMENT '有无担保：0无担保，1有担保',
  `guarantee_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '担保人名称',
  `guarantee_money` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '担保金额',
  `guarantee_type` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '担保形式',
  `is_guarantee_creditor` tinyint NULL DEFAULT 0 COMMENT '是否是保证债权，默认0不是、1是',
  `is_lawsuiting` tinyint NULL DEFAULT 0 COMMENT '是否正在进行诉讼、仲裁，默认0不是、1是',
  `is_has_legal_instrument` tinyint NULL DEFAULT 0 COMMENT '是否有生效法律文书，默认0不是、1是',
  `legal_instrument` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '法律文书',
  `is_apply_execute` tinyint NULL DEFAULT 0 COMMENT '是否申请执行，默认0不是、1是',
  `fact_and_reason` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '债权发生的基本事实和理由',
  `status` int NULL DEFAULT 0 COMMENT '状态:-1待申请、0待审核、2审核中、9审核完成 、-3删除',
  `create_user` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '创建用户',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_user` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '修改用户',
  `update_time` datetime NULL DEFAULT NULL COMMENT '修改时间',
  `remarks` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `step_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '申报表填写步骤id',
  `reviewer_id` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '审查人 id',
  `ensure_type` tinyint UNSIGNED NULL DEFAULT NULL COMMENT '保证形式：1：一般保证、2：连带保证',
  `is_has_agent` int NULL DEFAULT NULL,
  `add_temporary` int NULL DEFAULT NULL COMMENT '0暂存，1正常',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '破产-债权人表' ROW_FORMAT = DYNAMIC;

