DROP PROCEDURE IF EXISTS `NewProc`;
delimiter ;;
CREATE PROCEDURE `NewProc`(IN standardId CHAR ( 32 ))
BEGIN
	DECLARE isFlag INT DEFAULT TRUE;
	DECLARE
		pid CHAR ( 32 );#变量父ID
	DECLARE
		treeNum LONGTEXT;#变量树编号
	DECLARE
		isHas INT;#变量是否存在
	DECLARE
		tempIds LONGTEXT;#变量ID集合
	DECLARE
		treeLevel int DEFAULT 1;#变量数层级
	DECLARE
		def CHAR ( 32 );#元素
	DECLARE
		defs LONGTEXT;#元素集合
	DECLARE
		indexs int DEFAULT 0;#排序
	DECLARE
		pTreeNum LONGTEXT;#父级treenum
	DECLARE
		nTreeNum LONGTEXT;#当前treenum
	DECLARE
		nnTreeNum LONGTEXT;#当前treenum
	#定义一个错误的变量,类型是整形，默认是0
	DECLARE t_error INTEGER DEFAULT 0;
	#捕获到sql的错误，就设置t_error为1
	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;
	#开启事务
	START TRANSACTION;
-- 查询standardId下的顶级pid
	SELECT
		id INTO pid 
	FROM
		sys_standard_step_copy1
	WHERE
		standard_id = standardId 
		AND ISNULL( parent_id ) = 1 
		AND `status` = 0;
-- 查询standardId下的顶级treeNum
	SELECT
		step_tree_num INTO treeNum 
	FROM
		sys_standard_step_copy1 
	WHERE
		standard_id = standardId 
		AND ISNULL( parent_id ) = 1 
		AND `status` = 0;
-- 查询是否存在重复的treeNum，是重新选号，否使用原treeNum
	SELECT
		count( 1 ) INTO isHas 
	FROM
		sys_standard_step_copy1 
	WHERE
		step_tree_num = treeNum
		AND id != pid
		AND status=0;
	IF
		isHas > 0 OR treeNum IS NULL THEN
			SET treeNum = (
			SELECT
				IFNULL( MAX( step_tree_num ) + 1, 1 ) AS step_tree_num 
			FROM
				sys_standard_step_copy1 
			WHERE
				ISNULL( parent_id ) = 1 
				AND ISNULL( step_tree_num ) = 0 
			);
	-- 编号补5位
	SET treeNum =(SELECT LPAD(treeNum,5,0));
	-- 修改
	UPDATE sys_standard_step_copy1 set step_tree_num=treeNum,step_level=treeLevel WHERE id =pid;
	END IF;
	-- 根据pid循环递归排序并编号
  -- 设置group_concat长度
	SET SESSION group_concat_max_len = 1024000;
	-- 给临时变量赋默认值
	SELECT pid,treeNum,treeLevel;
	SET tempIds = pid;
	-- 递归修改level和treeNum
	WHILE tempIds IS NOT NULL DO
		-- 拼接参数
		SELECT GROUP_CONCAT(id) INTO tempIds FROM sys_standard_step_copy1 WHERE FIND_IN_SET(parent_id,tempIds)>0;
		-- 设置层级+1
		SET treeLevel=treeLevel+1;
		SELECT tempIds,treeLevel;
		-- 修改层级level
		UPDATE sys_standard_step_copy1 SET step_level=treeLevel WHERE id in (tempIds) ;
		-- 修改treeNum;
		-- 将tempIds给到变量defs 然后处理defs 每遍历一遍就去掉该值直至循环完毕 在此过程中处理数据 即根据id找到父节点的treeNum拼接(SELECT LPAD(index++,5,0)) index待声明 注意在每次循环完时重置 index的值
	END WHILE;
	
	#如果捕获到错误
	IF t_error=1 THEN
			#回滚
			ROLLBACK;
	ELSE
			#提交
			COMMIT;
	END IF;
END
;;
delimiter ;

