DROP PROCEDURE IF EXISTS `showTreeNodes`;
delimiter ;;
CREATE PROCEDURE `showTreeNodes`()
BEGIN
 DECLARE Level int ;
 drop TABLE IF EXISTS tmpLst;
 CREATE TABLE tmpLst (
  id VARCHAR(32),
  nLevel int,
  sCort varchar(8000)
 );
 
 Set Level=0 ;
 INSERT into tmpLst SELECT id,Level,ID FROM sys_standard_step WHERE parent_id is null and step_level = 0 
	AND standard_id = "eac5beb80cc442509bcef034d3d1c3e0";
 WHILE ROW_COUNT()>0 DO
  SET Level=Level+1 ;
  INSERT into tmpLst
   SELECT A.id,Level,concat(B.sCort,A.id) FROM sys_standard_step A,tmpLst B
    WHERE  A.parent_id=B.ID AND B.nLevel=Level-1  ;
 END WHILE;
END
;;
delimiter ;

